import React, { useEffect, useState } from 'react';
import styles from './HomeCta.module.css';
import HomeStyles from './css/HomeLanding.module.css';
import WatchList from './WatchList'; // Import the WatchList function
import { Watch } from './DataStructure.js'; // Import the Watch type from your types file
import Carousel from './Carousel';

function Card({watch}: Watch) {
    return (
        <div className={styles.Card}>
            <image className={styles.CardImage}></image>
            <div className={styles.CardTag}>watch.Name</div>
            <div className={styles.CardTag}>Price</div>
            <div className={styles.CardTag}>Range</div>
            <div className={styles.CardTag}>Brand</div>
            <div className={styles.CardTag}>Available</div>
        </div>
    );
}

const HomeCards: React.FC<{ watchlist: Watch[] }> = ({ watchlist }) => {

    return (
        <div className={styles.CardContainer}>
            {watchlist.map((watch, index) => (<Card key={index} watch={watch}/>))}
        </div>
    );
}

function HomeCta() {
    const [watchList, setWatchList] = useState<Watch[]>([]);
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        // Fetch watch list data and update state
        const fetchWatchData = async () => {
            try {
                const data = await WatchList(); // Assuming WatchList is a function that fetches the watch list data
                setWatchList(data); // Assuming data is an array of Watch objects
            } catch (error) {
                console.error('Error fetching watch list:', error);
            }
        };

        fetchWatchData();

        // Event listener for scroll
        window.addEventListener('scroll', handleScroll);

        return () => {
            // Clean up the event listener on component unmount
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleScroll = () => {
        // Check if the element is in the viewport
        const element = document.querySelector(`.${styles.HomeCta}`);
        if (element) {
            const bounding = element.getBoundingClientRect();
            const topInView = bounding.top >= 0 && bounding.top <= window.innerHeight;
            setIsVisible(topInView);
        }
    };

    return (
        <div className={`${styles.HomeCtaContainer}`}>
            <div className={`${styles.HomeCta} `}>
                <div className={` ${styles.Title} ${isVisible ? styles.Visible : ''}`}>Close To You</div>
                <HomeCards watchlist={watchList.slice(0, 3)}></HomeCards>
                <HomeCards watchlist={watchList.slice(3, 6)}></HomeCards>
            </div>
        </div>
    );
}

export default HomeCta;
