import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { Watch } from './DataStructure.js';
import styles from './Carousel.css'

const Carousel: React.FC<{ watchlist:Watch[] }> = ({ watchlist }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Show all slides
    slidesToScroll: 3,
    autoplay: true,
    autoplaySpeed: 2000,
    cssEase: 'linear',
    pauseOnHover: false,
    pauseOnFocus: false
  };

  return (
    <div className={styles.carousel}>
      {watchlist.map((watch, index) => (
        <div key={index} className={styles.slide}>
          {/* Render your carousel item content here */}
          <img src={watch.imageUrl} alt={watch.name} />
        </div>
      ))}
    </div>
  );
};

export default Carousel;
