import React, { useEffect, useRef } from 'react';
import styles from './Carousel.module.css';
import { Watch } from './types';

const Carousel: React.FC<{ watchlist: Watch[] }> = ({ watchlist }) => {
  const carouselRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const carousel = carouselRef.current;
    if (carousel) {
      const slides = carousel.querySelectorAll(`.${styles.slide}`);
      let currentIndex = 0;

      const animateCarousel = () => {
        const nextIndex = (currentIndex + 1) % slides.length;
        slides[currentIndex].classList.remove(styles.active);
        slides[nextIndex].classList.add(styles.active);
        currentIndex = nextIndex;
      };

      const intervalId = setInterval(animateCarousel, 5000); // Adjust interval duration as needed

      return () => clearInterval(intervalId);
    }
  }, [watchlist]);

  return (
    <div className={styles.carousel} ref={carouselRef}>
      {watchlist.map((watch, index) => (
        <div key={index} className={`${styles.slide} ${index === 0 ? styles.active : ''}`}>
          {/* Render your carousel item content here */}
          <img src={watch.imageUrl} alt={watch.name} />
        </div>
      ))}
    </div>
  );
};

export default Carousel;
