import React from 'react';
import Navbar from './NavBar';
import Footer from './Footer';
import styles from './css/RetailerPage.module.css';
import { useLocation } from 'react-router-dom';

interface Photo {
    photo_reference: string;
  }

  interface OpeningHours {
    open_now: boolean;
    weekday_text: string[];
  }


  interface Retailer {
    place_id: string;
    name: string;
    formatted_address: string;
    formatted_phone_number: string;
    website: string;
    rating: number;
    user_ratings_total: number;
    geometry: {
      location: {
        lat: number;
        lng: number;
      };
    };
    opening_hours: {
      open_now: boolean;
      weekday_text: string[];
    };
    reviews: {
      author_name: string;
      rating: number;
      text: string;
      time: number;
    }[];
    photos: {
      photo_reference: string;
      height: number;
      width: number;
    }[];
  }

const RetailerPage: React.FC = () => {
  const location = useLocation();
  const state = location.state as Retailer;
  const retailer = (location.state as { retailer: Retailer })?.retailer;

  const getImageUrl = (photoReference: string, maxWidth: number = 1600) => {
    const apiKey = 'YOUR_API_KEY'; // Replace with your API key
    return `https://maps.googleapis.com/maps/api/place/photo?maxwidth=${maxWidth}&photoreference=${photoReference}&key=${apiKey}`;
  };

  const formatTime = (time: string) => {
    const hours = time.substring(0, 2);
    const minutes = time.substring(2);
    return `${hours}:${minutes}`;
  };

  if (!retailer) {
    return <div>No retailer data available</div>;
  }

  return (
    <div>
      <Navbar />
      <div className={styles.Retailer}>
        {retailer.photoUrl ? (
          <img className={styles.storeImg} src={retailer.photoUrl} alt={retailer.name} />
        ) : (
          <div className={styles.storeImg}>No image available</div>
        )}
        <div className={styles.storeData}>
          <div className={styles.Name}>{retailer.name}</div>
          <div className={styles.openingHours}>
          <ul>
        {retailer.opening_hours.weekday_text.map((day, index) => (
          <li key={index}>{day}</li>
        ))}
      </ul>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default RetailerPage;
