import React from 'react';
import styles from './HomeCta.module.css';
import WatchList from './WatchList';
import { BestForYou } from './WatchList';
import { useEffect, useState } from 'react';
import '@splidejs/splide/dist/css/themes/splide-default.min.css'; // Import Splide CSS
import { Splide, SplideSlide } from '@splidejs/react-splide'; // Import Splide components

function HomeCta() {
    const [watchList, setWatchList] = useState<JSX.Element[]>([]);;

    useEffect(() => {
        async function fetchData() {
            const list = await WatchList();
            setWatchList(list);
        }
        fetchData();
    }, []);
    useEffect(() => {
        new Splide('.watch-list-slider').mount(); // Initialize Splide slider
    }, [watchList]); // Run useEffect whenever watchList changes
    return (
        <div>
        <div className={styles.HomeCta}>
            <div className={styles.Title}> Near From You </div>
            <button className={styles.SeeMore}>See more</button>
        </div>
        <Splide className="watch-list-slider"> {/* Wrap watchList inside Splide */}
            {watchList.map((watch, index) => (
                <SplideSlide key={index}>{watch}</SplideSlide>
            ))}
        </Splide>
        <div className={styles.HomeCta}>
            <div className={styles.Title}> Best for you </div>
            <button className={styles.SeeMore}>See more</button>
        </div>
    </div>
    )
}

export default HomeCta;
