import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import { Watch } from './DataStructure.js';
import styles from './Carousel.module.css';

const Carousel: React.FC<{ watchlist: Watch[] }> = ({ watchlist }) => {
  const settings = {
    dots: false,
    infinite: true,
    speed: 9000,
    slidesToShow: 3,
    slidesToScroll: 1, // Change to scroll one slide at a time
    autoplay: true,
    autoplaySpeed: 0,
    cssEase: 'linear',
    pauseOnHover: false,
    pauseOnFocus: false,
  };

  return (
    <Slider {...settings}>
      {watchlist.map((watch, index) => (
        <div key={index} className={styles.slide}>
         <div className={styles.slideContent} style={{ backgroundImage: `url(${watch.Url})` }}>
            <div className={styles.slideHeader}>
              <div className={styles.Name}>{watch.Name}</div>
              <div className={styles.Range}>{watch.Range}</div>
            </div>
          </div>
          {/* Render your carousel item content here */}
        </div>
      ))}
    </Slider>
  );
};

export default Carousel;
