// src/Favorites.tsx
import React, { useRef } from 'react';
import Slider from 'react-slick';
import styles from './css/Favorites.module.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

// Importing images
import image1 from './css/img/store_1.jpg';
import image2 from './css/img/store_2.jpg';
import image3 from './css/img/store_3.jpg';

const NextArrow = (props: any) => {
  const { className, onClick } = props;
  return (
    <div
      className={`${className} ${styles.arrowButton}`}
      onClick={onClick}
    >
      &gt;
    </div>
  );
};

const PrevArrow = (props: any) => {
  const { className, onClick } = props;
  return (
    <div
      className={`${className} ${styles.arrowButton}`}
      onClick={onClick}
    >
      &lt;
    </div>
  );
};

const Favorites: React.FC = () => {
  const sliderRef = useRef<Slider>(null); // Create a reference to the slider

  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Show 3 images at a time
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />
  };

  const images = [
    {
      src: image1,
      description: 'Description for Store Image 1',
      price: '$100'
    },
    {
      src: image2,
      description: 'Description for Store Image 2',
      price: '$150'
    },
    {
      src: image3,
      description: 'Description for Store Image 3',
      price: '$200'
    },
    {
      src: image1,
      description: 'Description for Store Image 1',
      price: '$100'
    },
    {
      src: image2,
      description: 'Description for Store Image 2',
      price: '$150'
    },
    {
      src: image3,
      description: 'Description for Store Image 3',
      price: '$200'
    }
  ];

  const handleArrowClick = () => {
    if (sliderRef.current) {
      sliderRef.current.slickNext(); // Move to the next slide
    }
  };

  return (
    <div className={styles.sliderContainer}>

      <Slider ref={sliderRef} {...sliderSettings}>
        {images.map((image, index) => (
          <div key={index} className={styles.slide}>
            <div className={styles.imageContainer}>
              <img src={image.src} alt={`Slide ${index}`} className={styles.sliderImage} />
            </div>
            <p className={styles.description}>{image.description}</p>
            <p className={styles.price}>{image.price}</p>
          </div>
        ))}
      </Slider>
      <button className={styles.arrowButton} onClick={handleArrowClick}>
        &gt;
      </button>
    </div>
  );
};

export default Favorites;
