// src/Favorites.tsx
import React from 'react';
import Slider from 'react-slick';
import styles from './css/Favorites.module.css';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

// Importing images
import image1 from './css/img/store_1.jpg';
import image2 from './css/img/store_2.jpg';
import image3 from './css/img/store_3.jpg';

const Favorites: React.FC = () => {
  const sliderSettings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 3, // Show 3 images at a time
    slidesToScroll: 1,
    nextArrow: <div style={{ fontSize: '30px', lineHeight: '1.5' }}>›</div>,
    prevArrow: <div style={{ fontSize: '30px', lineHeight: '1.5' }}>‹</div>
  };

  const images = [
    {
      src: image1,
      description: 'Description for Store Image 1'
    },
    {
      src: image2,
      description: 'Description for Store Image 2'
    },
    {
      src: image3,
      description: 'Description for Store Image 3'
    }
  ];

  return (
    <div className={styles.sliderContainer}>
      <Slider {...sliderSettings}>
        {images.map((image, index) => (
          <div key={index} className={styles.slide}>
            <div className={styles.imageContainer}>
              <img src={image.src} alt={`Slide ${index}`} className={styles.sliderImage} />
            </div>
            <p className={styles.description}>{image.description}</p>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Favorites;
